from indy import anoncreds, crypto, did, ledger, pool, wallet
from indy.error import ErrorCode, IndyError
import json
import sys
import asyncio

pool_name             = 'SOFIE'
pool_genesis_txn_path = "/home/sofie/Indy_Testbed_Setup/config/SOFIE/pool_transactions_genesis"
aueb_trustee_id       = "aueb_trustee"
aueb_wallet_pass      = ""
professor_id          = "professor"
professor_wallet_pass = ""
professor_master_secr = "master_secret"
cred_def_id = "KecCwQfSjPPYWhpZprpxqK:3:CL:KecCwQfSjPPYWhpZprpxqK:2:AUEB-job-certificate:1.2:AUEBCERT1"
cred_def_json = '{"ver":"1.0","id":"KecCwQfSjPPYWhpZprpxqK:3:CL:KecCwQfSjPPYWhpZprpxqK:2:AUEB-job-certificate:1.2:AUEBCERT1","schemaId":"KecCwQfSjPPYWhpZprpxqK:2:AUEB-job-certificate:1.2","type":"CL","tag":"AUEBCERT1","value":{"primary":{"n":"","s":"","r":{"position":"","master_secret":"","full_name":""},"rctxt":"","z":""}}}'
schema_id = "KecCwQfSjPPYWhpZprpxqK:2:AUEB-job-certificate:1.2"


async def run():
    try:
        print("\nConnecting to the pool")
        pool_config = json.dumps({"genesis_txn": str(pool_genesis_txn_path)})
        await pool.set_protocol_version(2)
        pool_handle = await pool.open_pool_ledger(pool_name, pool_config)

        print("\nOpening AUEB trustee wallet")
        aueb_wallet_config = json.dumps({"id": aueb_trustee_id})
        aueb_wallet_credentials = json.dumps({"key": aueb_wallet_pass})
        aueb_wallet = await wallet.open_wallet(aueb_wallet_config, aueb_wallet_credentials)
        aueb_dids = json.loads(await did.list_my_dids_with_meta(aueb_wallet))
        aueb_did =  aueb_dids[0]['did']
        
        print("\nOpening professor wallet")
        professor_wallet_config = json.dumps({"id": professor_id})
        professor_wallet_credentials = json.dumps({"key": professor_wallet_pass})
        professor_wallet = await wallet.open_wallet(professor_wallet_config, professor_wallet_credentials)
        professor_dids = json.loads(await did.list_my_dids_with_meta(professor_wallet))
        professor_did =  professor_dids[0]['did']


        print('\nAUEB is creating a Credential Offer for professor')
        cred_offer_json = await anoncreds.issuer_create_credential_offer(aueb_wallet, cred_def_id)

        print('\nProfessor creates Credential Request for the given credential offer')
        (cred_req_json, cred_req_metadata_json) = await anoncreds.prover_create_credential_req(professor_wallet, professor_did, cred_offer_json, cred_def_json, professor_master_secr)


        print('\nAUEB creates Credential for Credential Request')
        cred_values_json = json.dumps({
            'full_name': {"raw": "Nikos Fotiou", "encoded": "1139481716457488690172217916278103335"},
            'position': {"raw": "Professor", "encoded": "1"}
        })
        (cred_json, _, _) = await anoncreds.issuer_create_credential(aueb_wallet, cred_offer_json, cred_req_json, cred_values_json, None, None)

        print('\nProfessor processes and stores Credential')
        await anoncreds.prover_store_credential(professor_wallet, None, cred_req_metadata_json, cred_json, cred_def_json, None)

        print('\nClosing wallet and pool')
        await wallet.close_wallet(professor_wallet)
        await pool.close_pool_ledger(pool_handle)

    except IndyError as e:
        print('Error occurred: %s' % e)



def main():
    loop = asyncio.get_event_loop()
    loop.run_until_complete(run())
    loop.close()


if __name__ == '__main__':
    main()
